catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }
    var ga = new GlideAjax('getVehTrackerContact'); // getVehTrackerContact is the script include class 
    ga.addParam('sysparm_name', 'fetchContactName'); // fetchContactName is the script include method 
    ga.addParam('sysparm_user_sys_id', g_form.getValue('number')); //  Set parameter sysparm_user_sys_id that inhabits 'assigned_to' field and grabs the record that is tied
    ga.getXML(getVehTrackName);
    /* Call getRITMContact.getContactName() with the value in sysparm_user_sys_id  
        and use the callback function getVehTrackName() to return the result when ready */

}

// the callback function for returning the result from the server-side code
function getVehTrackName(response) {
    var answer = response.responseXML.documentElement.getAttribute("answer");
    alert(answer);
	g_form.setValue('user_name', answer);
}
