sys_script_include.script:
var getVehTrackerContact = Class.create();
getVehTrackerContact.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	fetchContactName: function(){
		var vehTrackName = this.getParameter('sysparm_user_sys_id');
		var resultName = '';

		var grUserInfo = new GlideRecord('sys_user');
		grUserInfo.addQuery('sys_id', vehTrackName); 
		grUserInfo.query(); 

		if (grUserInfo.next()){

			resultName = grUserInfo.getValue('user_name'); 

		} 
		return resultName; 
	}, 
    type: 'getVehTrackerContact'
});
